mapper.select:
SELECT *
FROM "TstCasePriority" o
WHERE o.id = #{id}
  AND o."orgId" = #{orgId}

mapper.delete:
UPDATE "TstCasePriority"
SET disabled     = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.update:
UPDATE "TstCasePriority"
SET "defaultVal" = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

UPDATE "TstCasePriority"
SET ordr = #{ordr},
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}
