mapper.select:
SELECT o.*
FROM "CustomFieldOption" o
WHERE o.id = #{id}
  AND o."fieldId" = #{fieldId}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "CustomFieldOption"
SET label = #{label},
    "defaultVal" = #{defaultVal},
    "updateTime" = NOW()
where id = #{id}
  AND "orgId" = #{orgId}

UPDATE "CustomFieldOption"
SET deleted = TRUE
where id = #{id}
  AND "orgId" = #{orgId}

mapper.select:
SELECT max(o.ordr)
FROM "CustomFieldOption" o

WHERE o."fieldId" = #{fieldId}
  AND o."orgId" = #{orgId}
  AND NOT o.deleted
  AND NOT o.disabled


SELECT *
FROM "CustomFieldOption" o
WHERE o.ordr < #{ordr}
  AND o."fieldId" = #{fieldId}
  AND o."orgId" = #{orgId}
  AND NOT o.deleted
ORDER BY o.ordr DESC
LIMIT 1


SELECT *
FROM "CustomFieldOption" o
WHERE o.ordr > #{ordr}
  AND o."fieldId" = #{fieldId}
  AND o."orgId" = #{orgId}
  AND NOT o.deleted
ORDER BY o.ordr ASC
LIMIT 1

mapper.update:
UPDATE "CustomFieldOption"
SET "defaultVal" = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "fieldId" = #{fieldId}
  AND "orgId" = #{orgId}

UPDATE "CustomFieldOption"
SET "defaultVal" = FALSE,
    "updateTime" = NOW()
WHERE "fieldId" = #{fieldId}
  AND "orgId" = #{orgId}
