mapper.select:
SELECT *
FROM "IsuPrioritySolution" o

WHERE o.id = #{id}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "IsuPrioritySolution"
SET name = #{name},
    disabled = #{disabled},
    "updateTime" = NOW()

WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.delete:
UPDATE "IsuPrioritySolution"
SET disabled     = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.update:
UPDATE "IsuPrioritySolution"
SET "defaultVal" = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.select:
SELECT *
FROM "IsuPrioritySolution" o
         JOIN "TstProject" p ON p."issuePrioritySolutionId" = o.id
WHERE p.id = #{projectId}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "TstProject"
SET "issuePrioritySolutionId" = #{solutionId},
    "updateTime" = NOW()

WHERE id = #{projectId}
  AND "orgId" = #{orgId}
