mapper.select:
SELECT * FROM "IsuQuery" o WHERE o."projectId"=#{prjId} AND "userId"=#{userId}
AND o.name LIKE '%${keywords}%'
AND NOT o.disabled AND NOT o.deleted ORDER BY o."createTime" ASC

SELECT *
FROM "IsuQuery" o

WHERE o.id = #{id}
  AND o."userId" = #{userId}

mapper.update:
UPDATE "IsuQuery"
SET name = #{model.name},
    "updateTime" = NOW()

WHERE id = #{model.id}
  AND "userId" = #{userId}

mapper.delete:
UPDATE "IsuQuery"
SET disabled     = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "userId" = #{userId}
