mapper.select:
SELECT *
FROM "IsuTypeSolution" o

WHERE o.id = #{id}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "IsuTypeSolution"
SET name = #{name},
    disabled = #{disabled},
    "updateTime" = NOW()

WHERE id = #{id}
  AND "orgId" = #{orgId}

UPDATE "IsuTypeSolution"
SET disabled     = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

UPDATE "IsuTypeSolution"
SET "defaultVal" = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.select:
SELECT *
FROM "IsuTypeSolution" o
         JOIN "TstProject" p ON p."issueTypeSolutionId" = o.id
WHERE p.id = #{projectId}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "TstProject"
SET "issueTypeSolutionId" = #{solutionId},
    "updateTime" = NOW()

WHERE id = #{projectId}
  AND "orgId" = #{orgId}
