mapper.select:
SELECT o.*, src.label "srcStatusName", dict.label "dictStatusName"
FROM "IsuWorkflowTransition" o
         JOIN "IsuStatus" src ON o."srcStatusId" = src.id
         JOIN "IsuStatus" dict ON o."dictStatusId" = dict.id

WHERE o.id = #{id}
  AND o."orgId" = #{orgId}

mapper.update:
UPDATE "IsuWorkflowTransition"
SET name = #{name},
    "actionPageId" = #{actionPageId},
    "srcStatusId" = #{srcStatusId},
    "dictStatusId" = #{dictStatusId},
    "updateTime" = NOW()

WHERE id = #{id}
  AND "workflowId" = #{workflowId}
  AND "orgId" = #{orgId}

mapper.select:
SELECT o."projectRoleId"
FROM "IsuWorkflowTransitionProjectRoleRelation" o
WHERE o."workflowTransitionId" = #{id}
  AND o."orgId" = #{orgId}

ORDER BY o.id ASC
