mapper.select:
SELECT * FROM "TstOrgGroup" grp WHERE grp."orgId" = #{orgId}
AND grp.name LIKE '%${keywords}%'
AND grp.id NOT IN
#{item}
AND NOT grp.deleted AND NOT grp.disabled ORDER BY grp.id ASC

SELECT *
FROM "TstOrgGroup" grp

WHERE grp.id = #{id}
  AND grp."orgId" = #{orgId}

mapper.update:
UPDATE "TstOrgGroup"
SET name = #{name},
    disabled = #{disabled},
    "updateTime" = NOW()

WHERE id = #{id}
  AND "orgId" = #{orgId}

mapper.delete:
UPDATE "TstOrgGroup"
SET deleted      = TRUE,
    "updateTime" = NOW()
WHERE id = #{id}
  AND "orgId" = #{orgId}
