mapper.select:
SELECT o.*, usr.nickname "userName", prj.name "projectName", ver.label "verName" FROM "TstPlan" o
JOIN "TstUser" usr ON usr.id=o."userId" JOIN "TstProject" prj ON prj.id=o."projectId" LEFT JOIN "TstVer" ver ON ver.id=o."verId"
WHERE o."projectId" = #{projectId}
AND o.name LIKE '%${keywords}%'
AND o.status = #{status}
AND NOT o.deleted ORDER BY o."createTime" DESC

SELECT o.*, usr.nickname "userName", prj.name "projectName", ver.label "verName" FROM "TstPlan" o
JOIN "TstUser" usr ON usr.id=o."userId" JOIN "TstProject" prj ON prj.id=o."projectId" LEFT JOIN "TstVer" ver ON ver.id=o."verId"
WHERE o.id = #{id}
AND o."projectId"=#{projectId}

mapper.update:
UPDATE "TstPlan"
SET deleted = TRUE
WHERE id = #{id}
  AND "projectId" = #{projectId}
